    private void onayButonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_onayButonActionPerformed

        String tarihG = tarih.getText();
        String kiraGider = kira.getText();
        String elektrikGider = elektrik.getText();
        String suGider = su.getText();
        String personelGider = personel.getText();
        int toplam = Integer.parseInt(kiraGider) + Integer.parseInt(elektrikGider) + Integer.parseInt(suGider) + Integer.parseInt(personelGider);
        String toplamYaz = Integer.toString(toplam);
        FileOperations.dosyaYaz("Kira", kiraGider, tarihG);
        FileOperations.dosyaYaz("Elektrik", elektrikGider, tarihG);
        FileOperations.dosyaYaz("Su Gideri", suGider, tarihG);
        FileOperations.dosyaYaz("Personel Gideri", personelGider, tarihG);
        FileOperations.dosyaYaz("Toplam", toplamYaz, tarihG);
        toplamSonuc.setText("Toplam : " + toplamYaz + "tl.");

    }//GEN-LAST:event_onayButonActionPerformed

